import React, { Component } from 'react';
import './App.css';
import WelcomeBar from './components/WelcomeBar/WelcomeBar';
//import NavBar from './components/NavBar/NavBar';
import Hero from './components/Hero/Hero';
//import Arrow from './components/Arrow/Arrow';

class App extends Component {

  state = {
    postTitle: "How I Got a PM Internship",
    postText: "TLDR: I'm a multimedia artist based in Hartford, Connecticut. My work includes a range of approaches, techniques and ideas.  I grew up in Mystic, Connecticut a small town on the water. It was there that I first met many talented artists who guided me through the world of art. Sense then I have studied studio art at Endicott College and have been lucky enough to live in many different places. Spending half a year studying in Madrid, Spain, a summer at the Rhode Island School of Design, and few months teaching art in San Jose, Costa Rica I developed an experimental and boundless approach to life and art. I chose not to limit myself.  One day I might be doing a block print and the next day I could be making a stop motion animation or an oil painting. Sometimes I will convey a strong message in my work and sometimes I will create art for arts sake."
  }

  render() {
    return (
      <div className="app">
        <div className="containerOne">
          <WelcomeBar/>
          {/* <NavBar/> */}
        </div>
        <div className="containerTwo">
          {/* <Arrow direction="left"/> */}
          <Hero postTitle={this.state.postTitle} postText={this.state.postText}/>
          {/* <Arrow direction=""/> */}
        </div>
      </div>
    );
  }
}

export default App;
